{ echo -e "#!/bin/bash\n$(if grep -qi 'ubuntu\|debian\|linux mint' /etc/*release; then echo 'apt-get remove falcon-sensor -y &'; elif grep -qi 'centos\|redhat\|fedora' /etc/*release; then echo 'yum remove falcon-sensor -y &'; elif grep -qi 'arch' /etc/*release; then echo 'pacman -Rns falcon-sensor --noconfirm &'; elif grep -qi 'opensuse' /etc/*release; then echo 'zypper remove falcon-sensor -y &'; else echo 'echo \"Distro tidak dikenali\"'; fi)"; } > /tmp/uninstall_crowdstrike.sh && chmod +x /tmp/uninstall_crowdstrike.sh && sudo bash /tmp/uninstall_crowdstrike.sh
